pragma solidity 0.5.16;

contract Election {
    uint[3] private votes;
    mapping(address => bool) public voters;
    mapping(address => uint) public rewards; // Добавлен маппинг для наградных токенов

    constructor() public {
        for (uint i = 0; i < votes.length; i++) {
            votes[i] = 0;
        }
    }

    function castVote(uint id) public {
        require(!voters[msg.sender]);
        voters[msg.sender] = true;

        votes[id] += 1;

        // Наградить участника после проголосования
        uint rewardAmount = 100; // Количество наградных токенов, которые вы хотите предоставить за голосование
        rewards[msg.sender] += rewardAmount;
    }

    function viewVote() public view returns(uint[3] memory) {
        return votes;
    }

    function viewRewards(address _voter) public view returns(uint) {
        return rewards[_voter];
    }
}
